import React ,{ Component, useEffect, useState } from 'react'
import axios from 'axios'
// import {useSelector, useDispatch} from 'react-redux'

function Dashboard() {
    // const dispatch = useDispatch()
    const [imageList, setImageList] = useState()
    const [postsFetched, setPostsFetched] = useState(false);

    const [state, setState] = useState({
        title: '',
        image: null
    });

    // const getItems=async()=>{
    //     // fetch('http://localhost:8000/api/images/')
    //     // .then(results => results.json())
    //     // .then(results => setImageList({items:results}), console.log(imageList));
    //     const response = await fetch('http://localhost:8000/api/images/')
    //     const results = await response.json()
    //     setImageList(results)
    // };

    // getItems();

    useEffect(() => {
        async function fetchData() {
            // You can await here
            const response = await fetch('http://localhost:8000/api/images/')
            setImageList(await response.json())
          }
          fetchData();
          setPostsFetched(true);
    }, [postsFetched])


    const handleChange = (e) => {
        setState({
            ...state,
            [e.target.id]: e.target.value
        })
      };

    const handleImageChange = (e) => {
        setState({
            ...state,
            image: e.target.files[0]
        })
      };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(state);
        let form_data = new FormData();
        form_data.append('image', state.image, state.image.name);
        form_data.append('title', state.title);
        // form_data.append('content', this.state.content);
        let url = 'http://localhost:8000/api/images/';
        axios.post(url, form_data, {
          headers: {
            'content-type': 'multipart/form-data'
          }
        })
            .then(res => {
              console.log(res.data);
            })
            .catch(err => console.log(err))


        setState({title: ''});
        // this.setState({image: ''});
      };


  return (
    <div className="App">
        <form onSubmit={handleSubmit}>
          <p>
            <input type="text" placeholder='Title' id='title' value={state.title} onChange={handleChange} required/>
          </p>
          <p>
            <input type="file"
                   id="image"
                   accept="image/png, image/jpeg"  onChange={handleImageChange} required/>
          </p>
          <input type="submit"/>
        </form>
      </div>
  )
}

export default Dashboard
